/** Helper macro for simple iMap array types. */
#define FIO_IMAP_ALWAYS_CMP_FALSE(a, b) (0)
/** Helper macro for simple iMap array types. */
#define FIO_IMAP_SIMPLE_CMP(a, b) ((a)[0] == (b)[0])
/** Helper macro for simple iMap array types. */
