  fprintf(stderr, "===============\n");
  FIO_NAME_TEST(stl, sort)();
  fprintf(stderr, "===============\n");
  FIO_NAME_TEST(stl, url)();
  fprintf(stderr, "===============\n");
  FIO_NAME_TEST(stl, glob_matching)();
  fprintf(stderr, "===============\n");
  FIO_NAME_TEST(stl, imap_core)();
  fprintf(stderr, "===============\n");
  FIO_NAME_TEST(stl, state)();
